'use client';

import { cn } from '@/lib/utils';
import { ReactNode } from 'react';

interface OrbitingCirclesProps {
  children: ReactNode;
  className?: string;
  radius?: number;
  duration?: number;
  delay?: number;
  reverse?: boolean;
  path?: boolean;
}

/**
 * OrbitingCircles - Creates orbiting elements around a center point
 * Used for tech stack visualizations
 */
export function OrbitingCircles({
  children,
  className,
  radius = 100,
  duration = 20,
  delay = 0,
  reverse = false,
  path = true,
}: OrbitingCirclesProps) {
  return (
    <>
      {/* Orbit path (optional visual) */}
      {path && (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          version="1.1"
          className="pointer-events-none absolute inset-0 h-full w-full"
        >
          <circle
            className="stroke-zinc-800 stroke-1"
            cx="50%"
            cy="50%"
            r={radius}
            fill="none"
            strokeDasharray="4 4"
          />
        </svg>
      )}

      {/* Orbiting element */}
      <div
        style={
          {
            '--duration': `${duration}s`,
            '--delay': `-${delay}s`,
            '--radius': `${radius}px`,
          } as React.CSSProperties
        }
        className={cn(
          'absolute flex h-full w-full transform-gpu items-center justify-center',
          reverse ? '[animation-direction:reverse]' : '',
          className
        )}
      >
        <div
          className={cn(
            'absolute flex items-center justify-center',
            'animate-[orbit_var(--duration)_linear_infinite]',
            '[animation-delay:var(--delay)]'
          )}
          style={{
            transform: `rotate(0deg) translateX(${radius}px) rotate(0deg)`,
          }}
        >
          {children}
        </div>
      </div>
    </>
  );
}

interface OrbitingIconProps {
  icon: ReactNode;
  label?: string;
  className?: string;
}

/**
 * OrbitingIcon - Styled icon container for orbit elements
 */
export function OrbitingIcon({ icon, label, className }: OrbitingIconProps) {
  return (
    <div
      className={cn(
        'group relative flex h-12 w-12 items-center justify-center rounded-xl border border-zinc-800 bg-zinc-900/80 backdrop-blur-sm shadow-lg transition-all duration-300 hover:border-brand-primary/50 hover:shadow-brand',
        className
      )}
    >
      <div className="text-zinc-400 group-hover:text-brand-primary transition-colors">
        {icon}
      </div>

      {/* Tooltip label */}
      {label && (
        <div className="absolute -bottom-8 left-1/2 -translate-x-1/2 opacity-0 group-hover:opacity-100 transition-opacity">
          <span className="whitespace-nowrap text-xs font-mono text-zinc-500 bg-zinc-900 px-2 py-1 rounded border border-zinc-800">
            {label}
          </span>
        </div>
      )}
    </div>
  );
}

interface TechOrbitProps {
  className?: string;
}

/**
 * TechOrbit - Pre-built tech stack solar system
 * Showcases the full tech stack in an impressive orbital display
 */
export function TechOrbit({ className }: TechOrbitProps) {
  return (
    <div
      className={cn(
        'relative flex h-[500px] w-full items-center justify-center',
        className
      )}
    >
      {/* Center element */}
      <div className="absolute z-10 flex h-24 w-24 items-center justify-center rounded-full border border-zinc-700 bg-gradient-to-br from-zinc-900 to-zinc-800 shadow-2xl">
        <span className="text-xl font-bold text-white">PAI</span>
      </div>

      {/* Inner orbit - Core tech */}
      <OrbitingCircles radius={80} duration={25} path={true}>
        <OrbitingIcon
          icon={<ReactIcon />}
          label="React"
          className="h-10 w-10"
        />
      </OrbitingCircles>

      <OrbitingCircles radius={80} duration={25} delay={8} path={false}>
        <OrbitingIcon
          icon={<TypeScriptIcon />}
          label="TypeScript"
          className="h-10 w-10"
        />
      </OrbitingCircles>

      <OrbitingCircles radius={80} duration={25} delay={16} path={false}>
        <OrbitingIcon
          icon={<NextJsIcon />}
          label="Next.js"
          className="h-10 w-10"
        />
      </OrbitingCircles>

      {/* Outer orbit - Infrastructure */}
      <OrbitingCircles radius={150} duration={35} reverse={true} path={true}>
        <OrbitingIcon icon={<PythonIcon />} label="Python" />
      </OrbitingCircles>

      <OrbitingCircles radius={150} duration={35} delay={7} reverse={true} path={false}>
        <OrbitingIcon icon={<DockerIcon />} label="Docker" />
      </OrbitingCircles>

      <OrbitingCircles radius={150} duration={35} delay={14} reverse={true} path={false}>
        <OrbitingIcon icon={<AWSIcon />} label="AWS" />
      </OrbitingCircles>

      <OrbitingCircles radius={150} duration={35} delay={21} reverse={true} path={false}>
        <OrbitingIcon icon={<OpenAIIcon />} label="OpenAI" />
      </OrbitingCircles>

      <OrbitingCircles radius={150} duration={35} delay={28} reverse={true} path={false}>
        <OrbitingIcon icon={<PostgresIcon />} label="PostgreSQL" />
      </OrbitingCircles>
    </div>
  );
}

