interface TechStackGridProps {
  className?: string;
}

/**
 * TechStackGrid - Categorized display of technologies we use
 * Clean, informational grid with hover details
 */
export function TechStackGrid({ className }: TechStackGridProps) {
  return (
    <div className={cn('grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-5', className)}>
      {TECH_STACK.map((category, categoryIndex) => (
        <motion.div
          key={category.title}
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true, margin: '-50px' }}
          transition={{ duration: 0.4, delay: categoryIndex * 0.1 }}
          className="rounded-xl border border-zinc-800 bg-zinc-900/50 backdrop-blur-sm overflow-hidden"
        >
          {/* Category Header */}
          <div className="px-5 py-4 border-b border-zinc-800 bg-zinc-900/80">
            <h3 className="text-sm font-mono text-zinc-300 uppercase tracking-wider font-medium">
              {category.title}
            </h3>
          </div>

          {/* Tech Items */}
          <div className="p-4 space-y-1">
            {category.items.map((tech) => (
              <TechItemCard key={tech.name} tech={tech} />
            ))}
          </div>
        </motion.div>
      ))}
    </div>
  );
}

interface TechItemCardProps {
  tech: TechItem;
}

function TechItemCard({ tech }: TechItemCardProps) {
  const [isHovered, setIsHovered] = useState(false);

  return (
    <div
      className={cn(
        'group relative flex items-center gap-4 px-4 py-3 rounded-lg transition-all duration-200',
        'hover:bg-zinc-800/70'
      )}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      {/* Icon with brand color on hover */}
      <div
        className="flex-shrink-0 w-10 h-10 flex items-center justify-center rounded-xl transition-all duration-300 bg-zinc-800"
        style={{
          backgroundColor: isHovered ? `${tech.brandColor}15` : undefined,
          color: isHovered ? tech.brandColor : '#a1a1aa',
        }}
      >
        {tech.icon}
      </div>

      {/* Name & Description */}
      <div className="flex-1 min-w-0">
        <div className="flex items-center justify-between gap-2">
          <span
            className="text-base font-medium transition-colors duration-200"
            style={{ color: isHovered ? tech.brandColor : '#e4e4e7' }}
          >
            {tech.name}
          </span>
        </div>

        {/* Description - shows on hover */}
        <motion.div
          initial={false}
          animate={{
            height: isHovered ? 'auto' : 0,
            opacity: isHovered ? 1 : 0,
          }}
          transition={{ duration: 0.2 }}
          className="overflow-hidden"
        >
          <p className="text-sm text-zinc-400 pt-1 leading-relaxed">
            {tech.description}
          </p>
        </motion.div>
      </div>

      {/* Hover indicator with brand color */}
      <div
        className="w-2 h-2 rounded-full transition-all duration-300"
        style={{
          backgroundColor: isHovered ? tech.brandColor : '#3f3f46',
          boxShadow: isHovered ? `0 0 8px ${tech.brandColor}60` : 'none',
        }}
      />
    </div>
  );
}

